R"(
<!DOCTYPE html>
<html lang='es'>
<head>
  <meta http-equiv='Content-Type' content='text/html; charset=utf-8'>
  <title>Mando Universal WiFi Escornabot</title>
  <style>
    body {margin:0;padding:0;text-align:center;font-family:sans-serif;}
    .box {margin:0 auto;padding:1.79% 2%;line-height:0;}
    @media screen and (orientation:landscape){.box{height:95vh;width:85vh;}}
    @media screen and (orientation:portrait){.box{height:95vw;width:85vw;}}
    h1{font-size:6vmin;}
    .circle {border-radius:50%;border:1px black solid;display:inline-block;margin:1.79% 2%;width:28%;height:25%;color:white;cursor:pointer;}
    #n { background:blue;}
    #e { background:green;}
    #w { background:red;}
    #s { background:black;}
    #g { background:white;}
    .cajon {margin-top:2%;font-size:3.5vmin;border: 1px gray solid;height:4vmin;line-height:4vmin;color:gray;}
    noscript{color:red;}
    noscript h1{font-size:10vmin;}
    noscript h2{font-size:5vmin;}
    noscript hr{margin:10%;border:2px solid red;}
  </style>
  <script type='text/javascript' >
    var xhr = new XMLHttpRequest();
    var lbl = 'lista de comandos almacenados';
    function atender() {
      if(xhr.readyState === XMLHttpRequest.DONE && xhr.status === 200) {
        comando = xhr.responseText[0];
        lista = document.getElementById('comandos').innerHTML;
        if (lista == lbl)
          lista = '';
        switch (comando) {
          case 'n':
            cmd = "&uarr;";
            break;
          case 'w':
            cmd = "&#8630;";
            break;
          case 'e':
            cmd = "&#8631;";
            break;
          case 's':
            cmd = "&darr;";
            break;
          case 'nn':
            cmd = "#&uarr;";
            break;
          case 'ww':
            cmd = "#&#8630;";
            break;
          case 'ee':
            cmd = "#&#8631;";
            break;
          case 'ss':
            cmd = "#&darr;";
            break;
          case 'gg':
            cmd = "#&#8623;";
            break;
          default:
            cmd = "";
        }
        document.getElementById('comandos').innerHTML = lista + cmd + '&nbsp;';
        if (comando == 'g')
          document.getElementById('comandos').innerHTML = lbl;
      }
    }
    xhr.onreadystatechange = atender;
    function enviar(control, alternative) {
      var cmd = alternative ? (control.id + control.id) : control.id;
      xhr.open('GET', 'accion?comando=' + cmd);
      xhr.send();
    }
  </script>
</head>
<body>
  <noscript>
    <h1>JavaScript DESACTIVADO</h1>
    <h2>este mando lo necesita para funcionar :(</h2>
    <hr />
  </noscript>
  <div class='box'>
    <h1>MUWi Escornabot</h1>
    <div class='circle' id='n'>
      <img role="img" alt="avanzar" src="data:image/svg+xml;base64," onclick='enviar(this.parentElement, false' ondblclick='enviar(this.parentElement, true);' />
    </div><br />
    <div class='circle' id='w'>
      <img role="img" alt="girar a la izquierda" src="data:image/svg+xml;base64," onclick='enviar(this.parentElement, false);' ondblclick='enviar(this.parentElement, true);' />
    </div><div class='circle' id='g'>
      <img role="img" alt="ejecutar" src="data:image/svg+xml;base64," onclick='enviar(this.parentElement, false);' ondblclick='enviar(this.parentElement, true);' />
    </div><div class='circle' id='e'>
      <img role="img" alt="girar a la derecha" src="data:image/svg+xml;base64," onclick='enviar(this.parentElement, false);' ondblclick='enviar(this.parentElement, true);' />
    </div><br />
    <div class='circle' id='s'>
      <img role="img" alt="retroceder" src="data:image/svg+xml;base64," onclick='enviar(this.parentElement, false);' ondblclick='enviar(this.parentElement, true);' />
    </div>
    <div class='cajon' id='comandos'>lista de comandos almacenados</div>
  </div>
</body>
</html>
)";
